import './App.css';
import YourCards from './containers/YourCards'
import CardContainer from './containers/CardContainer'
import { Component } from 'react';

const cardUrl = 'https://deckofcardsapi.com/api/deck/new/draw/?count=52'

export default class App extends Component {



state = {
  cards: [],
  yourCards: []
}

componentDidMount() {
  fetch(cardUrl)
    .then(response => response.json())
    .then(data => 
      this.setState(
        { cards: data.cards }
      )
    )
}

checkCard = (card) => {
  if (this.state.yourCards.includes(card)){
    this.move(card)
  }
  else {
    console.log("add card")
  }
}

moveCard = (card, location) => {
  this.setState({
    yourCards: [...this.state.yourCards, card]
  })
};



  render() {
    return (
      <div className="App">
        <YourCards  yourCards={ this.state.yourCards } checkCard={ this.checkCard } />
        <CardContainer cards={ this.state.cards } checkCard={ this.checkCard } />
      </div>
    )
  }
}
